mapper.select:
select atta_id as attaId, atta_filename as attaFilename, atta_location as attaLocation, atta_size as attaSize, atta_downnum as attaDownnum, atta_describe as attaDescribe, atta_ori_filename as attaOriFilename from Attachment
atta_sourceid=#{sourceId}
and atta_source_type=#{sourceType}

mapper.delete:
delete from
Attachment


		atta_sourceid=#{sourceId}


		and atta_source_type=#{sourceType}

mapper.update:
update
	Attachment
set
	atta_sourceid=#{sourceId}
where
	atta_id=#{attachId}
