mapper.select:
select cour_id, cour_name, count(*) as num from Course inner join BbsPost on cour_id=bbpo_cour_id
bbpo_user_id=#{stuId}
and cour_id in
#{course.courId}
group by cour_id order by num desc

select count(*) as postNum from BbsPost
1=1
and bbpo_cour_id=#{courId}
and bbpo_user_id=#{teaId}

select
count(*)
from
BbsPost

	1= 1 			

		and bbpo_cour_id=#{courseId}


		and bbpo_cour_id in

			#{courseId}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbpo_user_departId=#{departId}



			and bbpo_title like '%${searchWord}%'


			and bbpo_user_name like '%${searchWord}%'



		and bbpo_user_id=#{userId}

select
count(*)
from
BbsPost

	bbpo_istop= 1 			

		and bbpo_cour_id=#{courseId}


		and bbpo_cour_id in

			#{courseId}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbpo_user_departId=#{departId}



			and bbpo_title like '%${searchWord}%'


			and bbpo_user_name like '%${searchWord}%'



		and bbpo_user_id=#{userId}

select
bbpo_id as bbpoId,
bbpo_cour_name as courseName,
bbpo_title as bbpoTitle,
bbpo_time as bbpoTime,
bbpo_visitnum as bbpoVisitnum,
bbpo_replynum as bbpoReplynum,
bbpo_updateTime as bbpoUpdateTime,
bbpo_isbest as bbpoIsbest,
bbpo_istop as bbpoIstop,
bbpo_user_name as userName
from
BbsPost

	bbpo_istop=1 			

		and bbpo_cour_id=#{courseId}


		and bbpo_cour_id in

			#{id}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbpo_user_departId=#{departId}



			and bbpo_title like '%${searchWord}%'


			and bbpo_user_name like '%${searchWord}%'



		and bbpo_user_id=#{userId}


		and bbpo_id>=(select min(bbpo_id) from (
			select bbpo_id from BbsPost 
			where bbpo_istop=1 			

				and bbpo_cour_id=#{courseId}


				and bbpo_cour_id in

					#{id}



				and bbpo_time >= #{startTime}


				and bbpo_time <= #{endTime}


				and bbpo_user_departId=#{departId}



					and bbpo_title like '%${searchWord}%'


					and bbpo_user_name like '%${searchWord}%'



				and bbpo_user_id=#{userId}

				limit #{startIndex},#{maxCount}) as temp)
		limit #{maxCount}

select
bbpo_id as bbpoId,
bbpo_cour_name as courseName,
bbpo_title as bbpoTitle,
bbpo_time as bbpoTime,
bbpo_visitnum as bbpoVisitnum,
bbpo_replynum as bbpoReplynum,
bbpo_updateTime as bbpoUpdateTime,
bbpo_isbest as bbpoIsbest,
bbpo_istop as bbpoIstop,
bbpo_user_name as userName
from
BbsPost

	bbpo_istop=0 			

		and bbpo_cour_id=#{courseId}


		and bbpo_cour_id in

			#{id}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbpo_user_departId=#{departId}



			and bbpo_title like '%${searchWord}%'


			and bbpo_user_name like '%${searchWord}%'



		and bbpo_user_id=#{userId}


		and bbpo_id>=(select min(bbpo_id) from (
			select bbpo_id from BbsPost 
			where bbpo_istop=0 			

				and bbpo_cour_id=#{courseId}


				and bbpo_cour_id in

					#{id}



				and bbpo_time >= #{startTime}


				and bbpo_time <= #{endTime}


				and bbpo_user_departId=#{departId}



					and bbpo_title like '%${searchWord}%'


					and bbpo_user_name like '%${searchWord}%'



				and bbpo_user_id=#{userId}

				limit #{startIndex},#{maxCount}) as temp)
		limit #{maxCount}

select
count(distinct bbpo_id)
from
BbsPost
inner join BbsReply on bbpo_id=bbre_bbpo_id		

	1=1 	

		and bbpo_cour_id in

			#{id}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbre_user_id=#{userId}

select
count(distinct bbpo_id)
from
BbsPost
inner join BbsReply on bbpo_id=bbre_bbpo_id		

	bbpo_istop=1 

		and bbpo_cour_id in

			#{id}



		and bbpo_time >= #{startTime}


		and bbpo_time <= #{endTime}


		and bbre_user_id=#{userId}

select distinct bbpo_id as bbpoId, bbpo_cour_name as courseName, bbpo_title as bbpoTitle, bbpo_user_name as userName, bbpo_time as bbpoTime, bbpo_visitnum as bbpoVisitnum, bbpo_replynum as bbpoReplynum, bbpo_updateTime as bbpoUpdateTime, bbpo_isbest as bbpoIsbest, bbpo_istop as bbpoIstop from BbsPost inner join BbsReply on bbpo_id=bbre_bbpo_id inner join Course on cour_id=bbpo_cour_id
bbpo_istop=1
and bbpo_cour_id in
#{id}
and bbpo_time >= #{startTime}
and bbpo_time <= #{endTime}
and bbre_user_id=#{userId}
limit
#{startIndex},
#{maxCount}

select distinct bbpo_id as bbpoId, bbpo_cour_name as courseName, bbpo_title as bbpoTitle, bbpo_user_name as userName, bbpo_time as bbpoTime, bbpo_visitnum as bbpoVisitnum, bbpo_replynum as bbpoReplynum, bbpo_updateTime as bbpoUpdateTime, bbpo_isbest as bbpoIsbest, bbpo_istop as bbpoIstop from BbsPost inner join BbsReply on bbpo_id=bbre_bbpo_id inner join Course on cour_id=bbpo_cour_id
bbpo_istop=0
and bbpo_cour_id in
#{id}
and bbpo_time >= #{startTime}
and bbpo_time <= #{endTime}
and bbre_user_id=#{userId}
limit
#{startIndex},
#{maxCount}

mapper.update:
update
	BbsPost
set
	bbpo_istop=#{topValue}
where
	bbpo_id=#{bbsPostId}

update
	BbsPost
set
	bbpo_isbest=#{bestValue}
where
	bbpo_id=#{bbsPostId}

update 
	BbsPost


		bbpo_user_name=#{userName},


		bbpo_user_departId=#{depaId},


		bbpo_cour_name=#{courName}



	1=1 

		and bbpo_user_id=#{userId}


		and bbpo_cour_id=#{courId}

mapper.select:
select bbpo_cour_id as courseId, count(*) as bbsPostNum from BbsPost inner join User on user_id=bbpo_user_id
user_type=1
and user_depa_id=#{departId}
and bbpo_cour_id in
#{courseId}
GROUP BY bbpo_cour_id
