SELECT
	d1.depa_id as depaId,
	d1.depa_name as depaName,
	d1.depa_type as depaType
FROM 
	Department as d1
WHERE

		d1.depa_id = #{depaId}
		OR depa_parent_id = #{depaId}
		OR depa_parent_id IN (
			SELECT depa2.depa_id FROM Department as depa2
			WHERE depa2.depa_parent_id = #{depaId}
		)


		d1.depa_id = #{depaId}
		OR d1.depa_parent_id = #{depaId}


		d1.depa_id = #{depaId}


		d1.depa_id IN (${depaIds})


	select * from Department 			 



		depa_type=#{_parameter}


order by depa_type


	select * from Department 			 



	depa_type<#{_parameter}


order by depa_type

select depa_id as depaId, depa_name as depaName, depa_type as depaType, depa_parent_id as depaParentId from Department
depa_id=#{_parameter}
limit 1


	SELECT 
		depa_id, depa_name, 
		depa_abbreviation, depa_code, 
		depa_parent_id, depa_type
	FROM Department



		depa_id = #{depaId}


		depa_name = #{depaName}


		depa_abbreviation = #{depaAbbreviation}


		depa_code = #{depaCode}


		depa_parent_id = #{depaParentId}


		depa_type = #{depaType}


group by depa_type,depa_id

SELECT depa_id AS depaId, depa_name AS depaName, depa_abbreviation AS depaAbbreviation, depa_code AS depaCode, depa_parent_id AS depaParentId, depa_type AS depaType FROM Department
depa_name LIKE CONCAT('%','${searchWord}','%' )
depa_abbreviation LIKE CONCAT('%','${searchWord}','%' )
depa_code LIKE CONCAT('%','${searchWord}','%' )
AND depa_type=#{depaType}
AND depa_parent_id=#{parentId}
AND depa_id=#{departId}
order by depa_type
LIMIT
#{startIndex},
#{maxCount}

SELECT
	 count(*) 
FROM
	Department


		depa_name  LIKE CONCAT('%','${searchWord}','%' )


		depa_abbreviation  LIKE CONCAT('%','${searchWord}','%' )


		depa_code  LIKE CONCAT('%','${searchWord}','%' )


		AND depa_type=#{depaType}


		AND depa_parent_id=#{parentId}


		AND depa_id=#{departId}
