mapper.update:
UPDATE
	EvaluateMethod
SET
	evme_name = #{evmeName},
	evme_pattern = #{evmePattern},
	evme_threhold = #{evmeThrehold}

		,evme_describe = #{evmeDescribe}

WHERE
	evme_id = #{evmeId}

mapper.insert:
INSERT
	INTO EvaluateMethod(
		evme_name,
		evme_pattern,
		evme_threhold

			,evme_describe

	) VALUES(
		#{evmeName},
		#{evmePattern},
		#{evmeThrehold}

			,#{evmeDescribe}

	)

mapper.select:
SELECT count(*) FROM EvaluateMethod as evme WHERE 1 = 1
AND evme_id = #{evmeId,jdbcType=INTEGER}
AND ( evme_name LIKE '%${evmeName}%' OR evme_describe LIKE '%${evmeName}%' )

select cour_id as courseId, evme_id as evaluateMethodId, evme_pattern as evaluatePattern, evme_threhold as threhold from Course inner join EvaluateMethod on evme_id=cour_tepa_id
cour_id in
#{courseId}
