mapper.select:
select cour_id, cour_name from Course inner join Re_SelectCource on cour_id=rsco_cour_id inner join User on rsco_user_id=user_id
rsco_verify=1 and rsco_valid=1 and cour_verify=1 and cour_state=1
and user_id=#{userId}
and cour_poston=#{bbsOpenStatus}

mapper.update:
