mapper.select:
select rsco_cour_id as courseId, rsco_user_depaName as depaName, rsco_grade_id as gradeId, rsco_grade_name as gradeName, count(rsco_id) as selectNum, sum(rsco_loginScore) as learnNum, sum(rsco_learnTimeScore) as learnTime, sum(rsco_bbsDiscussScore) as bbsPostNum from Re_SelectCource INNER JOIN Grade on rsco_grade_id=grad_id where 1=1
and grad_depa_id=#{departId}
and rsco_cour_id=#{courseId}
GROUP BY rsco_cour_id,rsco_grade_id
having selectNum>0
ORDER BY courseId,depaName,gradeName

select rsco_cour_id as courseId, sum(rsco_loginScore) as learnNum, sum(rsco_learnTimeScore) as learnTime from Re_SelectCource inner join User on user_id=rsco_user_id
user_type=1 and rsco_valid=1 and rsco_verify=1
and user_depa_id=#{departId}
and rsco_cour_id in
#{courseId}
GROUP BY rsco_cour_id

select rsco_user_id as stuId, user_realname as stuName, user_loginname as stuNum, rsco_grade_name as stuGrade, cour_id as courseId, cour_name as courseName, cour_credit as courseCredit, cour_cateIds as courCategory, cour_year as courYear, cour_term as courTerm, cour_studyPhase as courStudyPhase, cour_artScience as courArtScience, cour_type as courseType, rsco_totalScore as totalScore from Re_SelectCource inner join User on user_id=rsco_user_id inner join Course on cour_id=rsco_cour_id
user_type=1 and rsco_valid=1 and rsco_verify=1
and rsco_cour_id=#{courseId}
and user_depa_id=#{departId}
and user_clas_id=#{classId}
and user_clas_id in
#{clasId}
and rsco_cour_id in
#{course.courId}
and user_realname like '%${searchWord}%'
and user_loginname like '%${searchWord}%'
and rsco_time >= #{startTime}
and rsco_time <= #{endTime}
and rsco_totalScore<10
and rsco_totalScore>=10 and rsco_totalScore<20
and rsco_totalScore>=20 and rsco_totalScore<60
and rsco_totalScore>=60
and rsco_totalScore<60
and rsco_state=1
and rsco_state=0
order by rsco_grade_name,rsco_user_id

mapper.update:
update
	Re_SelectCource
set
	rsco_state=1,
	rsco_confirmId=#{confirmId}
where
	rsco_state=0 
	and rsco_user_id=#{userId}
	and rsco_cour_id=#{courseId}
