SELECT 
	COUNT(*)
FROM 
	User u



INNER JOIN
	Course c




	LEFT JOIN
		Class class ON class.clas_id = u.user_clas_id
	LEFT JOIN
		Grade g ON g.grad_id = class.clas_grad_id




u.user_verify =1 


	and u.user_type = #{searchModel.type}




and (u.user_realname like CONCAT('%','${searchWord}','%' )
or u.user_loginname like CONCAT('%','${searchWord}','%' )
)




	and u.user_depa_id = #{searchModel.deptId}




	and u.user_clas_id = #{searchModel.classId}




	and g.grad_id = #{searchModel.grade}




	and c.cour_id = #{searchModel.courseId}
	and c.cour_teacher_ids like CONCAT('%,',u.user_id,',%')

SELECT u.user_id as userId, u.user_realname as name, u.user_loginname as userNumber, u.user_email as email , u.user_phone_num as phoneNum, att.atta_id as userCoverPictureId, att.atta_location as attaLocation, att.atta_filename as attaFilename, d.depa_name as depaName
, c.cour_name as courseName
, u.user_cadas_exam_num as examNum, class.clas_name as className, g.grad_name as gradeName
FROM User u LEFT OUTER JOIN Attachment att ON att.atta_id = u.user_coverPictureId
INNER JOIN Course c
LEFT JOIN Department d ON d.depa_id = u.user_depa_id
LEFT JOIN Class class ON class.clas_id = u.user_clas_id LEFT JOIN Grade g ON g.grad_id = class.clas_grad_id
u.user_verify = 1
and u.user_type = #{searchModel.type}
and (u.user_realname like CONCAT('%','${searchWord}','%' ) or u.user_loginname like CONCAT('%','${searchWord}','%' ) )
and u.user_depa_id = #{searchModel.deptId}
and u.user_clas_id = #{searchModel.classId}
and g.grad_id = #{searchModel.grade}
and c.cour_id = #{searchModel.courseId} and c.cour_teacher_ids like CONCAT('%,',u.user_id,',%')
and u.user_verify = #{userVerify}
limit
#{startIndex},
#{maxCount}

