role_id=#{roleId}
and user_depa_id=#{depaId}

SELECT U.user_id as stuId, U.user_loginname as stuNum, U.user_realname as stuName, C.clas_name as stuClass, G.grad_name as stuGrade FROM User as U LEFT JOIN Class as C on C.clas_id = U.user_clas_id LEFT JOIN Grade as G on G.grad_id = C.clas_grad_id LEFT JOIN Department as D on D.depa_id = U.user_depa_id LEFT JOIN Re_SelectCource as RSC on RSC.rsco_user_id = U.user_id
rsco_valid=1 and rsco_verify=1
AND D.depa_id = #{departId}
AND G.grad_id = #{gradeId}
AND RSC.rsco_cour_id = #{courseId}
AND U.user_clas_id = #{classId}
AND U.user_realname like CONCAT('%','${studentName}','%')
AND U.user_type = 1
limit
#{startIndex},
#{maxCount}

SELECT COUNT(*) FROM User as U LEFT JOIN Class as C on C.clas_id = U.user_clas_id LEFT JOIN Grade as G on G.grad_id = C.clas_grad_id LEFT JOIN Department as D on D.depa_id = U.user_depa_id LEFT JOIN Re_SelectCource as RSC on RSC.rsco_user_id = U.user_id
rsco_valid=1 and rsco_verify=1
AND D.depa_id = #{departId}
AND G.grad_id = #{gradeId}
AND U.user_clas_id = #{classId}
AND RSC.rsco_cour_id = #{courseId}
AND U.user_realname like CONCAT('%','${studentName}','%')
AND U.user_type = 1

